function getBoxPlot(vect, idx){
    let q25 = quantile(vect, 0.25)
    let q50 = quantile(vect, 0.50)
    let q75 = quantile(vect, 0.75)

    let iqr = 1.5* (q75 - q25)
    let max = Math.max.apply(Math, vect)
    let min = Math.min.apply(Math, vect)

    let upperLimit = (max < q75 + iqr ? max : q75 + iqr)
    let lowerLimit = (min > q25 - iqr ? min : q25 - iqr)

    let outliers = []
    vect.forEach(e => {
        if (e > upperLimit ) {
            outliers.push(e)
        }
        if (e < lowerLimit ) {
            outliers.push(e)
        } 
    } )

    let boxData = new BoxData(idx, q25, q50, q75, [lowerLimit, upperLimit], outliers)

    return boxData
}

