function adjustData(dataSet, options) {
    if (Array.isArray(dataSet[0])) {
        dataSet = Matrix.checkMatrix(dataSet);
    } else if (Array.isArray(dataSet)) {
        dataSet = new Matrix(dataSet.map(a => {
            return [a];
        }));
    } else {
        throw new Error('Invalid data type for dataSet');
    }
    const {
        means = Stat.mean(dataSet),
        std = Stat.standardDeviation(dataSet, means, true)
    } = options;

    return [dataSet, means, std];
}

function normal(x) {
    if (!Array.isArray(x)) {
        throw new Error('The input should be an array');
    } else if (!Array.isArray(x[0])) {
        return vectorNormalization(x);
    } else {
        return matrixNormalization(x);
    }
}

function vectorNormalization(data) {
    let mean = computeMean(data);
    let std = computeVariance(data, true);

    let ans = new Array(data.length);
    if (std === 0) {
        for (let i = 0; i < ans.length; i++) {
            ans[i] = (data[i] - mean);
        }
    } else {
        for (let i = 0; i < ans.length; i++) {
            ans[i] = (data[i] - mean) / std;
        }
    }

    return ans;
}

function matrixNormalization(data) {
    let means = MatrixStat.mean(data, 1);
    let std = matrixStandardDeviation(data, means);

    let ans = new Array(data.length);
    for (let i = 0; i < data.length; i++) {
        ans[i] = new Array(data[i].length);
        for (let j = 0; j < data[i].length; j++) {
            if (std[i] === 0) {
                ans[i][j] = (data[i][j] - means[i]);
            } else {
                ans[i][j] = (data[i][j] - means[i]) / std[i];
            }
        }
    }
    return ans;
}

function matrixStandardDeviation(data, means) {
    return data.map(
        (row, i) => Math.sqrt(row.reduce(
            (acc, val) => acc + ((val - means[i]) * (val - means[i]))
            , 0) / (row.length - 1))
    );
}

function PCA(dataset, options = {}) {
    var {
        nComponents = 2,
        threshold = 1e-9,
        maxIterations = 100
    } = options;

    var eMatrix = _adjust(dataset, options);

    var r = eMatrix.rows;
    var c = eMatrix.columns;

    var T = Matrix.zeros(r, nComponents);
        var P = Matrix.zeros(c, nComponents);
        var eigenvalues = new Array(nComponents);

        for (let i = 0; i < nComponents; i++) {
            let tIndex = maxSumColIndex(eMatrix.clone().mulM(eMatrix));
            let t = eMatrix.getColumnVector(tIndex);

            let k = 0;
            let tNew = t.dot(t);
            for (let tOld = Number.MAX_SAFE_INTEGER; Math.abs(tOld - tNew) > threshold && k  < maxIterations; k++) {
                var p = getLoading(eMatrix, t);
                t = eMatrix.mmul(p);
                tOld = tNew;
                tNew = t.dot(t);
                //console.log(t.dot(t), tNew,tOld)

            }
            eigenvalues[i] = tNew;
            T.setColumn(i, t);
            P.setColumn(i, p);
            eMatrix.sub(t.mmul(p.transpose()));
        }
    return {T, P};
}

function getLoading(e, t) {
    var m = e.columns;
    var n = e.rows;

    var result = new Matrix(m, 1);

    var Bcolj = new Array(n);
    for (var i = 0; i < m; i++) {
        var s = 0;
        for (let k = 0; k < n; k++) {
            s += e.get(k, i) * t[k][0];
        }
        result.set(i, 0, s);
    }
    return result.mul(1 / result.norm());
}

function maxSumColIndex(data) {
    return data.sum('column').maxIndex()[0];
}

function _adjust(dataset, options) {
    dataset = new Matrix(dataset);
    if (options.center) {
        const means = mean(dataset);
        const stdevs = stdev(dataset, means, true);
        // this.means = means;
        dataset.subRowVector(means);
        if (options.scale) {
            for (var i = 0; i < stdevs.length; i++) {
                if (stdevs[i] === 0) {
                    throw new RangeError('Cannot scale the dataset (standard deviation is zero at index ' + i);
                }
            }
            // this.stdevs = stdevs;
            dataset.divRowVector(stdevs);
        }
    }
    return dataset;
};

function predictEllipse(x, y, n, alpha, nbPoints) {
    let variance = [computeVariance(x), computeVariance(y)];
    let means = [0,0];
    let elp = ellipse([0,0], variance, nbPoints)
    let c = 2 * (n-1)/n * (n+1)/(n-2);
    let F = Math.sqrt(c * 4.7374);
    elp = elp.map((point) => {
        return point.map((p, i) => F * p + means[i])
    })
    return elp;
}

function ellipse(center, std, nbPoints) {
    let vector = new Array(nbPoints);
    let jump = 2 * Math.PI / (nbPoints - 1);
    let t = 0;
    for (let i = 0; i < nbPoints; i++) {
        vector[i] = [center[0] + Math.sqrt(std[0]) * Math.cos(t), center[1] + Math.sqrt(std[1]) * Math.sin(t)];
        t += jump;
    }
    return vector;
}

function transpose(matrix) {
  return matrix[0].map((col, i) => matrix.map(row => row[i]));
}

