function getDescriptors(input) {
    let result = [];
    for (let i of input.meta.fields) {
        if (i === '') continue;
        let name = i.replace(/\./g, '_').toLowerCase();
        result.push({
            name,
            jpath: 'element.' + name,
        });
    }
    return result;
}

function cleanData(input, factor = 1) {
    var data = JSON.parse(JSON.stringify(input.data));
    if (input.errors.length > 0) {
        let errors = input.errors.filter((a) => a.row !== undefined);
        errors.sort((a, b) => a.row + b.row).forEach((e) => {
            data.splice(e.row, 1);
        });
    }

    return data.map((e, index) => {
        let result = {}
        let keys = Object.keys(e);
        for (let i = 1; i < keys.length; i++) {
            let name = keys[i].replace(/\./g, '_').toLowerCase();
            result[name] = Number(e[keys[i]]) * factor;
        };
        result['sampleid'] = e[''].replace(/\./g, '_').toLowerCase();;
        return result;
    })
}

