function completeCustomData(data, custom, customName) {
    let result = {};
    let labels = Object.keys(data[0]);
    let customKeys = Object.keys(custom).map(x => x.toLowerCase());
    let labelsLC = labels.map(x => x.toLowerCase());
    labelsLC.map((x, i) => (customKeys.indexOf(x) != -1) ? result[labels[i]] = custom[x] : result[labels[i]] = 0);
    result["_highlight"] = data.length + 1;
    result["_row"] = customName;
    return result;
}

function completeCustomMetadata(metadata, customName) {
    let result = {};
    let labels = Object.keys(metadata[0]);
    labels.map((x, i) => result[labels[i]] = null);
    result["SampleID"] = customName;
    result["_highlight"] = metadata.length + 1;
    result["_row"] = customName;
    return result;
}

