function Boxplot (title, boxData) {
  this.title = title,
  this.data = new Array(boxData)
}

function BoxData (x, q1, q2, q3, whiskers, outliers) {
    this.type = 'box',
    this.boxes = new Array(   
    {'x' : x,    
    'Q1' : q1, 
    'Q2' : q2, 
    'Q3' : q3, 
    'whiskers' : whiskers, 
    'outliers' : outliers})
}

