function BoxData (x, q1, q2, q3, whiskers, outliers) {
    this.type = 'box',
    this.orientation = 'y'
    this.maxBoxWidth = 10,
    this.defaultStyle = {
	  "outlierLineWidth" : 2,
	  "outlierLineColor" : 'rgb( 0, 255, 255 )',
	  "outlierFillColor" : 'rgb( 0, 255, 0 )',
	  "outlierFillOpacity" : 0
	}
    this.boxes = new Array(   
    {'x' : x,    
    'Q1' : q1, 
    'Q2' : q2, 
    'Q3' : q3, 
    'whiskers' : whiskers, 
    'outliers' : outliers})
}

function quantile(data, q) {
    data=Array_Sort_Numbers(data);
    var pos = ((data.length) - 1) * q;
    var base = Math.floor(pos);
    var rest = pos - base;
    if( (data[base+1]!==undefined) ) {
        return data[base] + rest * (data[base+1] - data[base]);
    } else {
        return data[base];
    }
}

function Array_Sort_Numbers(inputarray){
  return inputarray.sort(function(a, b) {
    return a - b;
  });
}

//let vect = [.26, 0.34, 0.70, 1.75, 50.57, 1.55, 0.08, 0.42, 0.50, 3.20, 0.15, 0.49, 0.95, 0.24, 1.37, 0.17, 6.98, 0.10, 0.94, 0.38]

function getBoxPlot(vect, idx){
    let q25 = quantile(vect, 0.25)
    let q50 = quantile(vect, 0.50)
    let q75 = quantile(vect, 0.75)

    let iqr = 1.5* (q75 - q25)
    let max = Math.max.apply(Math, vect)
    let min = Math.min.apply(Math, vect)

    let upperLimit = (max < q75 + iqr ? max : q75 + iqr)
    let lowerLimit = (min > q25 - iqr ? min : q25 - iqr)

    let outliers = []
    vect.forEach(e => {
        if (e > upperLimit ) {
            outliers.push(e)
        }
        if (e < lowerLimit ) {
            outliers.push(e)
        } 
    } )

    let boxData = new BoxData(idx, q25, q50, q75, [lowerLimit, upperLimit], outliers)

    return boxData
}
